import React from "react";
import PropTypes from "prop-types";
import Panel from "@vkontakte/vkui/dist/components/Panel/Panel";
import PanelHeader from "@vkontakte/vkui/dist/components/PanelHeader/PanelHeader";
import Button from "@vkontakte/vkui/dist/components/Button/Button";
import Group from "@vkontakte/vkui/dist/components/Group/Group";
import Cell from "@vkontakte/vkui/dist/components/Cell/Cell";
import Div from "@vkontakte/vkui/dist/components/Div/Div";
import Avatar from "@vkontakte/vkui/dist/components/Avatar/Avatar";
import { Footer, Progress, InfoRow } from "@vkontakte/vkui";
import connect from "@vkontakte/vk-connect";
import "./style.css";
import odin from "../img/1.jpg";
import dva from "../img/2.jpg";
var x = 40;
function plus() {
  x = x + 10;

  return (
    <Panel id={id}>
      <PanelHeader>Заголовок</PanelHeader>
      {fetchedUser && (
        <Group title="Вас зовут">
          <Cell
            before={
              fetchedUser.photo_200 ? (
                <Avatar src={fetchedUser.photo_200} />
              ) : null
            }
            description={
              fetchedUser.city && fetchedUser.city.title
                ? fetchedUser.city.title
                : ""
            }
          >
            {`${fetchedUser.first_name} ${fetchedUser.last_name}`}
          </Cell>
        </Group>
      )}
      <Group>
        <Div>
          <InfoRow title="Default">
            <Progress value={x} />
          </InfoRow>
        </Div>
        <Div className="button">
          <Button className="button" size="xl" level="2" onClick={plus}>
            Кнопка увеличения
          </Button>
        </Div>
      </Group>
      <Group title="Заголовок" className="qqq">
        <table>
          <tr>
            <td>
              <img src={odin} onClick={go} data-to="persik" />
            </td>
            <td>
              <img src={dva} onClick={go} data-to="persik2" />
            </td>
          </tr>
        </table>
      </Group>

      <Group title="Заголовок доната">
        <Div>
          <InfoRow className="marga" title="На реализацию новых кнопок">
            <Progress value={3} />
          </InfoRow>
        </Div>
        <Div>
          <a className="align">(2859 руб. /100 000 руб.)</a>
        </Div>
        <Div className="button">
          <Button className="button" size="xl" level="2" onClick={sendChtoto}>
            Кнопка доната
          </Button>
        </Div>
      </Group>
      <Footer>
        <a href="https://vk.com/galaprogastyle/">Ссылка на создателя</a>
      </Footer>
    </Panel>
  );
}
const Home = ({ id, go, fetchedUser }) => {
  const receivingUserId = 154522988;
  const sendChtoto = () => {
    connect.send("VKWebAppOpenPayForm", {
      action: "transfer-to-user",
      app_id: 7154173,
      params: {
        description: "На развитие сервиса",
        user_id: receivingUserId
      }
    });
  };

  return (
    <Panel id={id}>
      <PanelHeader>Заголовок</PanelHeader>
      {fetchedUser && (
        <Group title="Вас зовут">
          <Cell
            before={
              fetchedUser.photo_200 ? (
                <Avatar src={fetchedUser.photo_200} />
              ) : null
            }
            description={
              fetchedUser.city && fetchedUser.city.title
                ? fetchedUser.city.title
                : ""
            }
          >
            {`${fetchedUser.first_name} ${fetchedUser.last_name}`}
          </Cell>
        </Group>
      )}
      <Group>
        <Div>
          <InfoRow title="Default">
            <Progress value={x} />
          </InfoRow>
        </Div>
        <Div className="button">
          <Button className="button" size="xl" level="2" onClick={plus}>
            Кнопка увеличения
          </Button>
        </Div>
      </Group>
      <Group title="Заголовок" className="qqq">
        <table>
          <tr>
            <td>
              <img src={odin} onClick={go} data-to="persik" />
            </td>
            <td>
              <img src={dva} onClick={go} data-to="persik2" />
            </td>
          </tr>
        </table>
      </Group>

      <Group title="Заголовок доната">
        <Div>
          <InfoRow className="marga" title="На реализацию новых кнопок">
            <Progress value={3} />
          </InfoRow>
        </Div>
        <Div>
          <a className="align">(2859 руб. /100 000 руб.)</a>
        </Div>
        <Div className="button">
          <Button className="button" size="xl" level="2" onClick={sendChtoto}>
            Кнопка доната
          </Button>
        </Div>
      </Group>
      <Footer>
        <a href="https://vk.com/galaprogastyle/">Ссылка на создателя</a>
      </Footer>
    </Panel>
  );
};

Home.propTypes = {
  id: PropTypes.string.isRequired,
  go: PropTypes.func.isRequired,
  fetchedUser: PropTypes.shape({
    photo_200: PropTypes.string,
    first_name: PropTypes.string,
    last_name: PropTypes.string,
    city: PropTypes.shape({
      title: PropTypes.string
    })
  })
};

export default Home;
